VALUES
	(1, 'সহিহ বুখারী', 'ইসলামে হাদিস বা হাদিস শাস্ত্র বলা হয় সেই জ্ঞান সম্পর্কে যার সাহায্যে রাসুল্লাহ (সঃ) এর কথা, কাজ, অনুমোদন ইত্যাদি সম্পর্কে জ্ঞান লাভ করা যায়। মুহাদ্দিসগন হাদিসের বিশুদ্ধতা রক্ষার জন্য আপ্রান চেষ্টা ও সাধনা করেছেন। তারই প্রমান হচ্ছে ইমাম বুখারী (রহঃ) এই গ্রন্থ সহিহুল বুখারী। সহিহ বুখারি হচ্ছে সমস্ত হাদিস গ্রন্থের মধ্যে সবচাইতে বিশুদ্ধ হাদিস গ্রন্থ। সহিহ বুখারিতে ৭৫৬৩ টি হাদিস রয়েছে এবং এই সব গুলো হাদিসই সহিহ্‌।', NULL, NULL, NULL, NULL, NULL, NULL, 0, '2013-11-01 06:55:33', '2013-11-01 06:55:33', NULL, NULL, NULL, 'bukhari', 'ইমাম ইসমাইল বুখারি ', NULL, 7534),
	(2, 'সহিহ মুসলিম', 'ইসলামে হাদিস বা হাদিস শাস্ত্র বলা হয় সেই জ্ঞান সম্পর্কে যার সাহায্যে রাসুল্লাহ (সঃ) এর কথা, কাজ, অনুমোদন ইত্যাদি সম্পর্কে জ্ঞান লাভ করা যায়। মুহাদ্দিসগন হাদিসের বিশুদ্ধতা রক্ষার জন্য আপ্রান চেষ্টা ও সাধনা করেছেন। তারই প্রমান হচ্ছে ইমাম বুখারী (রহঃ) এই গ্রন্থ সহিহুল বুখারী। সহিহ বুখারি হচ্ছে সমস্ত হাদিস গ্রন্থের মধ্যে সবচাইতে বিশুদ্ধ হাদিস গ্রন্থ। সহিহ বুখারিতে ৭৫৬৩ টি হাদিস রয়েছে এবং এই সব গুলো হাদিসই সহিহ্‌।', NULL, NULL, NULL, NULL, NULL, NULL, 0, '2014-09-10 06:55:33', '2014-09-10 06:55:33', NULL, NULL, NULL, 'muslim', 'ইমাম মুসলিম', NULL, 5400);
