INSERT INTO `sections` (`id`, `book_id`, `chapter_id`, `title`, `preface`, `createdAt`, `updatedAt`, `createdBy`, `updatedBy`, `deletedBy`, `number`, `sort_order`)
VALUES
	(1, 1, 1, 'আল্লাহ্‌র রসূল (সাল্লাল্লাহু আলাইহি ওয়া সাল্লাম)- এর প্রতি কীভাবে ওয়াহী [১] শুরু হয়েছিল।', 'এ মর্মে  আল্লাহ তা’আলার বাণীঃ “নিশ্চয়ই আমি আপনার প্রতি সেরূপ ওয়াহী প্রেরণ করেছি যেরূপ নূহ ও তাঁর পরবর্তী নবীদের প্রতি ওয়াহী প্রেরণ করেছিলাম”। (সূরা আন-নিসা ৪/১৬৩)', '2013-11-20 10:17:28', '2013-12-14 15:25:19', 'anon.', 'admin', 'anon.', '১/১. অধ্যায়ঃ', NULL),
	(2, 1, 1, '১/২. অধ্যায়ঃ', NULL, '2013-11-20 21:53:37', '2013-11-22 23:13:48', 'anon.', 'anon.', 'anon.', '১/২. অধ্যায়ঃ', NULL),
	(3, 1, 1, '১/৩. অধ্যায়ঃ', NULL, '2013-11-20 21:54:03', '2013-11-22 23:14:53', 'anon.', 'anon.', 'anon.', '১/৩. অধ্যায়ঃ', NULL),
	(4, 1, 1, '১/৪. অধ্যায়ঃ', NULL, '2013-11-20 21:55:03', '2013-11-22 23:15:40', 'anon.', 'anon.', 'anon.', '১/৪. অধ্যায়ঃ', NULL),
	(5, 1, 1, '১/৫. অধ্যায়ঃ', NULL, '2013-11-20 21:55:25', '2013-11-22 23:16:06', 'anon.', 'anon.', 'anon.', '১/৫. অধ্যায়ঃ', NULL),
	(6, 1, 1, '১/৬. অধ্যায়ঃ', NULL, '2013-11-20 21:55:49', '2013-12-07 15:49:21', 'anon.', 'admin', 'anon.', '১/৬. অধ্যায়ঃ', NULL),
	(7, 1, 5, 'নবী (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম)-এর বাণীঃ ইসলাম পাঁচটি স্তম্ভের উপর প্রতিষ্ঠিত।', 'আল্লাহর রসূল (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম)-এর বাণীঃ ইসলামের স্তম্ভ হচ্ছে পাঁচটিঃ মুখে স্বীকার এবং কাজে পরিণত করাই হচ্ছে ঈমান এবং তা বৃদ্ধি পায় ও হ্রাস পায়। [১] আল্লাহ তা’আলা বলেনঃ “যাতে তারা তাদের ঈমানের সঙ্গে ঈমান মজবুত করে নেয়-(সূরা ফাত্‌হ ৪৮/৪); আমরা তাদের সৎ পথে চলার শক্তি বাড়িয়ে দিয়েছিলাম- (সূরা কাহাফ ১৮/১৩); এবং যারা সৎপথে চলে আল্লাহ তাদের অধিক হিদায়াত দান করেন-(সূরা মারইয়াম ১৯/৭৬); এবং যারা সৎপথ অবলম্বন করে আল্লাহ তাদের হিদায়াত বাড়িয়ে দেন এবং তাদের সৎপথে চলার শক্তি বাড়িয়ে দেন- (সূরা মুহাম্মাদ ৪৭/১৭)। যাতে মু’মিনদের ঈমান বেড়ে যায়-(সূরা মুদদাস্‌সির ৭৪/৩১), আল্লাহ তা’আলা আরো বলেন, এটা তোমাদের মধ্যে কার ঈমান বাড়িয়ে দিল? যারা মু’মিন এ তো তাদের ঈমান বাড়িয়ে দেয়-(সূরা আত্‌-তাওবাহ ৯/১২৪), এবং তাঁর বাণী, “সুতরাং তোমরা তাদের ভয় কর ; একথা তাদের ঈমানের দৃঢ়তা বাড়িয়ে দিল”- (সূরা আল-ইমরান ৩/১৭৩)। “আর এতে তাদের ঈমান ও আনুগত্যই বাড়লো” –(সূরা আহ্‌যাব ৩৩/১৭৩)। “এতে তাদের ঈমান ও আনুগত্য আরও বৃদ্ধি পেল” –(সূরা আহ্‌যাব ৩৩/২২)।\r\nআর আল্লাহর জন্য ভালবাসা ও আল্লাহর জন্য ঘৃণা করা ঈমানের অংশ। ‘উমার ইব্‌নু ‘আবদুল ‘আযীয (রহঃ) ‘আদী ইব্‌নু ‘আদী (রহঃ)-এর নিকট এক পত্রে লিখেছিলেন, ‘ঈমানের কতকগুলো ফর্‌য, কতকগুলো হুকুম-আহকাম, বিধি-নিষেধ এবং সুন্নাত রয়েছে। যে এগুলো পূর্ণভাবে আদায় করে, তার ঈমান পূর্ণ হয়। আর যে এগুলো পূর্ণভাবে আদায় করে না, তার ঈমান পূর্ণ হয় না। আমি যদি বেঁচে থাকি তবে অচিরেই এগুলো তোমাদের নিকট ব্যক্ত করব, যাতে তোমরা তার উপর ‘আমল করতে পার। আর যদি আমার মৃত্যু হয় তাহলে জেনে রাখ, তোমাদের সাহচর্যে থাকার জন্য আমি আকাঙ্ক্ষিত নই’।\r\nইবরাহীম (‘আঃ) বলেন, ‘তবে এ তো কেবল চিত্ত প্রশান্তির জন্য’- (সূরা আল-বাক্বারাহ ২/২৬), মু’আয (রাঃ) বলেন, “এসো আমাদের সঙ্গে বস, কিছুক্ষণ ঈমানের আলোচনা করি”; ইব্‌নু মাস’উদ (রাঃ) বলেন, ‘ইয়াকীন হল পূর্ণ ঈমান’, ইব্‌নু ‘উমার (রাঃ) বলেন, ‘বান্দা প্রকৃত তাকওয়ায় পৌছতে পারে না, যতক্ষণ পর্যন্ত সে, মনে যে বিষয়ের সন্দেহের সৃষ্টি করে, তা পরিত্যাগ না করে’; মুজাহিদ (রহঃ) এ আয়াতের ব্যাখ্যায় বলেন, “অর্থাৎ হে মুহাম্মাদ (সাল্লাল্লাহু ‘আলাইহি ওয়া সাল্লাম) ! আমি আপনাকে এবং নূহকে একই ধর্মের আদেশ করেছি”- (সূরা শূরা ৪২/১৩), ইব্‌নু আব্বাস (রাঃ) বলেন, “অর্থাৎ পথ ও পন্থা”- (সূরা আল-মায়িদাহ ৫/৪৮)।', '2013-11-22 23:24:52', '2013-12-14 18:38:49', 'anon.', 'admin', 'anon.', '২/১. অধ্যায়ঃ', NULL),
	(8, 1, 5, 'তোমাদের দু’আ অর্থাৎ তোমাদের ঈমান।', 'এ মর্মে আল্লাহ তা’আলার বানীঃ  “বলে দিন, আমার প্রতিপালক তোমাদের একটুও পরোয়া করবেন না যদি তোমরা ‘ইবাদাত না কর”- (সূরাহ্‌ আল-ফুরক্বান ২৫/৭৭) । অভিধানে দু’আর অর্থ করা হয়েছেঃ “ঈমান”।', '2013-11-23 00:03:29', '2013-11-23 00:03:29', 'anon.', 'anon.', 'anon.', '২/২. অধ্যায়ঃ', NULL),
	(9, 1, 5, 'ঈমানের বিষয়সমূহ', 'আল্লাহ তা’আলার বাণীঃ “কোন পুণ্য নেই পূর্ব এবং পশ্চিম দিকে তোমাদের মুখ ফেরানোতে ; কিন্তু পুণ্য আছে কেউ ঈমান আনলে আল্লাহর উপর, আখিরাতের উপর, ফেরেশতাদের উপর, সকল কিতাবের উপর, আর সকল নবী-রসূলদের উপর, এবং অর্থ দান করলে আল্লাহর মহব্বতে আত্মীয়-স্বজন, ইয়াতিম, মিসকিন, মুসাফির, সাহায্যপ্রার্থী এবং দাস মুক্তির জন্য, সালাত কায়িম করলে, যাকাত দিলে, কৃত প্রতিশ্রুতি পূর্ণ করলে আর অভাবে, রোগে-শোকে ও যুদ্ধ বিভ্রাটে ধৈর্যধারণ করলে। এরাই হল প্রকৃত সত্যপরায়ণ, আর এরাই মুত্তাকী”- (আল-বাক্বারাহ ২/১৭৭)। “অবশ্যই সফলতা লাভ করেছে মুমিনগণ”-(সূরা মুমিনূন ২৩/১)।', '2013-11-23 00:05:05', '2013-12-14 20:27:01', 'anon.', 'admin', 'anon.', '২/৩. অধ্যায়ঃ', NULL),
	(10, 1, 5, 'সে-ই প্রকৃত মুসলিম, যার জিহবা ও হাত হতে অন্য মুসলিম নিরাপদ থাকে।', NULL, '2013-11-23 00:06:08', '2013-11-23 00:06:08', 'anon.', 'anon.', 'anon.', '২/৪. অধ্যায়ঃ', NULL);
